import React from 'react';
import { Box, Typography, Button, Container } from '@mui/material';

const HeroSection = () => {
  return (
    <Box
      sx={{
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#FFFFFF',
        color: '#1B2A44',
        textAlign: 'center',
        py: 8,
        position: 'relative',
      }}
    >
      <Container maxWidth="md">
        <Typography
          variant="h1"
          gutterBottom
          sx={{
            fontWeight: 700,
            fontSize: { xs: '2.5rem', md: '4rem' },
            lineHeight: 1.2,
            color: '#1B2A44',
            mb: 3,
          }}
        >
          Elevate Your Career and Business
        </Typography>
        <Typography
          variant="h5"
          sx={{
            fontWeight: 400,
            fontSize: { xs: '1.2rem', md: '1.5rem' },
            maxWidth: '700px',
            mx: 'auto',
            mb: 4,
            color: '#1B2A44',
          }}
        >
          Partnered with Zvertex AI, we deliver cutting-edge IT solutions to empower individuals and businesses for success.
        </Typography>
        <Button
          variant="contained"
          color="primary"
          size="large"
          href="https://zvertexai.com"
          target="_blank"
          sx={{
            bgcolor: '#00AEEF',
            color: '#FFFFFF',
            px: 4,
            py: 1.5,
            fontSize: '1.1rem',
            fontWeight: 600,
            borderRadius: '8px',
            '&:hover': {
              bgcolor: '#00AEEF',
              opacity: 0.9,
            },
          }}
        >
          Learn More
        </Button>
      </Container>
    </Box>
  );
};

export default HeroSection;
