import { createTheme } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    primary: { main: '#0A2540' }, // Midnight Blue
    secondary: { main: '#00D4FF' }, // Electric Cyan
    accent: { main: '#635BFF' }, // Indigo
    background: { default: '#FFFFFF', paper: '#F8F9FA' }, // White & Light Gray
    text: { primary: '#121826', secondary: '#6C757D' }, // Charcoal & Gray
  },
  typography: {
    fontFamily: "'Inter', sans-serif",
    h1: { fontWeight: 800, fontSize: '3.5rem', letterSpacing: '-0.02em' },
    h2: { fontWeight: 800, fontSize: '2.75rem', letterSpacing: '-0.01em' },
    h4: { fontWeight: 600, fontSize: '1.875rem' },
    h6: { fontWeight: 600, fontSize: '1.25rem' },
    body1: { fontSize: '1rem', fontWeight: 400 },
    button: { textTransform: 'none', fontWeight: 600, fontSize: '1rem' },
  },
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          borderRadius: 6,
          padding: '12px 24px',
          boxShadow: 'none',
          transition: 'all 0.3s ease',
          '&:hover': { boxShadow: '0 6px 16px rgba(0, 0, 0, 0.12)', transform: 'translateY(-2px)' },
        },
      },
    },
    MuiCard: {
      styleOverrides: {
        root: {
          borderRadius: 10,
          boxShadow: '0 8px 24px rgba(0, 0, 0, 0.05)',
          border: '1px solid #E9ECEF',
          transition: 'all 0.3s ease',
          '&:hover': { boxShadow: '0 12px 32px rgba(0, 0, 0, 0.1)' },
        },
      },
    },
  },
});

export default theme;
