import { createTheme } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    primary: { main: '#1B2A44' }, // Deep blue for headers, text
    secondary: { main: '#FFFFFF' }, // White for backgrounds, text on dark
    accent: { main: '#00AEEF' }, // Light blue for buttons, highlights
    background: { default: '#FFFFFF', paper: '#FFFFFF' }, // White for all pages
    text: { primary: '#1B2A44', secondary: '#1B2A44' }, // Consistent text color
  },
  typography: {
    fontFamily: "'Montserrat', sans-serif",
    h1: { fontSize: '2.5rem', fontWeight: 700, lineHeight: 1.2, color: '#1B2A44' }, // Hero titles
    h2: { fontSize: '2rem', fontWeight: 600, lineHeight: 1.3, color: '#1B2A44' }, // Section headers
    h5: { fontSize: '1.25rem', fontWeight: 400, lineHeight: 1.4, color: '#1B2A44' }, // Subtitles
    body1: { fontSize: '1rem', fontWeight: 400, lineHeight: 1.6, color: '#1B2A44' }, // Main content
    body2: { fontSize: '0.875rem', fontWeight: 400, lineHeight: 1.5, color: '#1B2A44' }, // Footer, small text
    button: { fontSize: '1rem', fontWeight: 600, lineHeight: 1.5, textTransform: 'none' },
  },
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          borderRadius: 8,
          padding: '12px 24px',
          backgroundColor: '#00AEEF',
          color: '#FFFFFF',
          transition: 'background-color 0.3s ease',
          '&:hover': {
            backgroundColor: '#0099CC', // Slightly darker blue on hover
          },
        },
        outlined: {
          borderColor: '#1B2A44',
          color: '#1B2A44',
          backgroundColor: '#FFFFFF',
          '&:hover': {
            backgroundColor: '#F0F8FF', // Light blue tint
            borderColor: '#1B2A44',
          },
        },
      },
    },
    MuiAppBar: {
      styleOverrides: {
        root: {
          backgroundColor: '#1B2A44',
          boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',
        },
      },
    },
    MuiMenuItem: {
      styleOverrides: {
        root: {
          fontSize: '1rem',
          color: '#1B2A44',
          backgroundColor: '#FFFFFF',
          '&:hover': {
            backgroundColor: '#F0F8FF',
          },
        },
      },
    },
    MuiTypography: {
      styleOverrides: {
        root: {
          color: '#1B2A44',
        },
      },
    },
    MuiLink: {
      styleOverrides: {
        root: {
          color: '#FFFFFF',
          textDecoration: 'none',
          '&:hover': {
            color: '#00AEEF', // Accent color on hover
          },
        },
      },
    },
  },
});

export default theme;
