import React from 'react';
import { Container, Typography, Box, Button, Grid } from '@mui/material';
import HeroSection from '../components/HeroSection';
import ContactForm from '../components/ContactForm';

const Home = () => {
  return (
    <>
      <HeroSection />
      <Container sx={{ py: 5 }}>
        {/* About Us */}
        <Box sx={{ textAlign: 'center', mb: 5 }}>
          <Typography variant="h4" gutterBottom>About Hono Technology</Typography>
          <Typography variant="body1" sx={{ maxWidth: '800px', mx: 'auto' }}>
            At Hono Technology, we redefine IT excellence by delivering innovative solutions that empower businesses and individuals. Partnered with Zvertex AI, we provide cutting-edge tools to accelerate your career and business growth in a dynamic digital world.
          </Typography>
        </Box>

        {/* Services */}
        <Box sx={{ mb: 5 }}>
          <Typography variant="h4" gutterBottom align="center">Our Services</Typography>
          <Grid container spacing={3}>
            {[
              'App Development', 'Data Engineering & Analysis', 'IT Consulting Services', 'Cloud Computing',
              'Full Stack Development', 'IT Security', 'Web Development', 'Quality Assurance'
            ].map((service) => (
              <Grid item xs={12} sm={6} md={3} key={service}>
                <Box sx={{ p: 2, bgcolor: '#f5f5f5', borderRadius: 2 }}>
                  <Typography variant="h6">{service}</Typography>
                </Box>
              </Grid>
            ))}
          </Grid>
        </Box>

        {/* Testimonials */}
        <Box sx={{ mb: 5, textAlign: 'center' }}>
          <Typography variant="h4" gutterBottom>Testimonials</Typography>
          <Typography variant="body1" sx={{ maxWidth: '800px', mx: 'auto', fontStyle: 'italic' }}>
            "Hono Technology transformed our business with their innovative IT solutions and exceptional support." - Jane Doe, CEO
          </Typography>
        </Box>

        {/* Consulting Pitch */}
        <Box sx={{ bgcolor: 'primary.main', color: 'white', py: 5, textAlign: 'center', borderRadius: 2 }}>
          <Typography variant="h4" gutterBottom>Consulting for Every Business</Typography>
          <Typography variant="h6" sx={{ mb: 3 }}>
            The best IT consulting firm you can rely on for growth and success.
          </Typography>
          <Button variant="contained" color="secondary" component={Link} to="/contact">
            Get Started
          </Button>
        </Box>

        {/* Newsletter */}
        <Box sx={{ mt: 5, textAlign: 'center' }}>
          <Typography variant="h5" gutterBottom>Subscribe for Latest News</Typography>
          <Box component="form" sx={{ maxWidth: '400px', mx: 'auto' }}>
            <TextField label="Email Address" fullWidth variant="outlined" sx={{ mb: 2 }} />
            <Button variant="contained" color="primary">Subscribe</Button>
          </Box>
        </Box>
      </Container>
      <ContactForm />
    </>
  );
};

export default Home;
