import React from 'react';
import { Box, Typography, Button, Container } from '@mui/material';

const HeroSection = () => {
  return (
    <Box
      sx={{
        bgcolor: 'primary.main',
        color: 'white',
        py: 12,
        textAlign: 'center',
        background: 'linear-gradient(135deg, #1E3A8A 40%, #F59E0B 100%)',
      }}
    >
      <Container>
        <Typography variant="h1" gutterBottom sx={{ textShadow: '0 2px 4px rgba(0, 0, 0, 0.2)' }}>
          Innovate with Hono Technology
        </Typography>
        <Typography variant="h4" gutterBottom sx={{ fontWeight: 300, maxWidth: '800px', mx: 'auto' }}>
          Transforming businesses with advanced IT solutions and Zvertex AI integration.
        </Typography>
        <Button
          variant="contained"
          color="secondary"
          href="https://zvertexai.com"
          target="_blank"
          size="large"
          sx={{ mt: 3 }}
        >
          Discover Zvertex AI
        </Button>
      </Container>
    </Box>
  );
};

export default HeroSection;
