import React from 'react';
import { Box, Typography, Button, Container } from '@mui/material';

const HeroSection = () => {
  return (
    <Box
      sx={{
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center',
        bgcolor: 'primary.main',
        background: 'linear-gradient(135deg, #0A2540 40%, #635BFF 100%)',
        color: 'white',
        textAlign: 'center',
        py: 8,
      }}
    >
      <Container>
        <Typography variant="h1" gutterBottom sx={{ maxWidth: '900px', mx: 'auto' }}>
          Pioneering IT Solutions for a Digital Future
        </Typography>
        <Typography variant="h4" sx={{ fontWeight: 400, maxWidth: '700px', mx: 'auto', mb: 4 }}>
          Partner with Hono Technology to unlock innovative IT strategies and Zvertex AI-powered growth.
        </Typography>
        <Button variant="contained" color="secondary" size="large" href="https://zvertexai.com" target="_blank">
          Explore Solutions
        </Button>
      </Container>
    </Box>
  );
};

export default HeroSection;
