import { createTheme } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    primary: { main: '#1E3A8A' }, // Deep Blue
    secondary: { main: '#F59E0B' }, // Warm Amber
    success: { main: '#10B981' }, // Emerald Green
    background: { default: '#F9FAFB', paper: '#FFFFFF' }, // Light Gray & White
    text: { primary: '#1F2937', secondary: '#6B7280' }, // Dark Gray & Medium Gray
  },
  typography: {
    fontFamily: "'Poppins', sans-serif",
    h1: { fontWeight: 700, fontSize: '3rem', lineHeight: 1.2 },
    h2: { fontWeight: 700, fontSize: '2.5rem', lineHeight: 1.3 },
    h4: { fontWeight: 700, fontSize: '1.75rem' },
    h6: { fontWeight: 500, fontSize: '1.25rem' },
    body1: { fontSize: '1.1rem', fontWeight: 300 },
    button: { textTransform: 'none', fontWeight: 500 },
  },
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          borderRadius: 8,
          padding: '10px 20px',
          transition: 'all 0.3s ease',
          '&:hover': { boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)' },
        },
      },
    },
    MuiCard: {
      styleOverrides: {
        root: {
          borderRadius: 12,
          boxShadow: '0 4px 20px rgba(0, 0, 0, 0.05)',
          transition: 'transform 0.3s ease',
          '&:hover': { transform: 'translateY(-5px)' },
        },
      },
    },
  },
});

export default theme;
