import React, { useState, useEffect } from 'react';
import { AppBar, Toolbar, Typography, Button, Container } from '@mui/material';
import { Link } from 'react-router-dom';

const Header = () => {
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => setScrolled(window.scrollY > 50);
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <AppBar
      position="fixed"
      sx={{
        bgcolor: scrolled ? 'primary.main' : 'transparent',
        boxShadow: scrolled ? '0 2px 12px rgba(0, 0, 0, 0.1)' : 'none',
        transition: 'background-color 0.3s ease',
      }}
    >
      <Container>
        <Toolbar sx={{ py: 2 }}>
          <Typography
            variant="h6"
            sx={{ flexGrow: 1, fontWeight: 800, color: scrolled ? 'white' : 'primary.main' }}
          >
            Hono Technology
          </Typography>
          <Button color="inherit" component={Link} to="/" sx={{ mx: 1, color: scrolled ? 'white' : 'primary.main' }}>
            Home
          </Button>
          <Button color="inherit" component={Link} to="/about" sx={{ mx: 1, color: scrolled ? 'white' : 'primary.main' }}>
            About
          </Button>
          <Button color="inherit" component={Link} to="/services" sx={{ mx: 1, color: scrolled ? 'white' : 'primary.main' }}>
            Services
          </Button>
          <Button color="inherit" component={Link} to="/careers" sx={{ mx: 1, color: scrolled ? 'white' : 'primary.main' }}>
            Careers
          </Button>
          <Button
            variant="contained"
            color="secondary"
            component={Link}
            to="/contact"
            sx={{ mx: 1, boxShadow: 'none' }}
          >
            Contact Us
          </Button>
        </Toolbar>
      </Container>
    </AppBar>
  );
};

export default Header;
