import { createTheme } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    primary: { main: '#1A2E45' }, // Deep Slate Blue
    secondary: { main: '#C94F00' }, // Deep Orange
    accent: { main: '#2E8B57' }, // Sea Green
    background: { default: '#FFFFFF', paper: '#F5F6F5' }, // White & Light Gray
    text: { primary: '#1A2E45', secondary: '#6B7280' }, // Deep Slate Blue & Gray
  },
  typography: {
    fontFamily: "'Montserrat', sans-serif",
    h1: { fontWeight: 800, fontSize: '3.5rem', letterSpacing: '-0.02em' },
    h2: { fontWeight: 800, fontSize: '2.5rem', letterSpacing: '-0.01em' },
    h4: { fontWeight: 600, fontSize: '1.75rem' },
    h6: { fontWeight: 600, fontSize: '1.25rem' },
    body1: { fontSize: '1rem', fontWeight: 400 },
    button: { textTransform: 'none', fontWeight: 600, fontSize: '1rem' },
  },
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          borderRadius: 8,
          padding: '12px 24px',
          boxShadow: '0 2px 8px rgba(0, 0, 0, 0.1)',
          transition: 'all 0.3s ease',
          '&:hover': { boxShadow: '0 6px 16px rgba(0, 0, 0, 0.2)', transform: 'translateY(-2px)' },
        },
      },
    },
    MuiCard: {
      styleOverrides: {
        root: {
          borderRadius: 12,
          boxShadow: '0 4px 16px rgba(0, 0, 0, 0.08)',
          transition: 'all 0.3s ease',
          '&:hover': { boxShadow: '0 8px 24px rgba(0, 0, 0, 0.15)' },
        },
      },
    },
  },
});

export default theme;
